INSERT INTO MUSCLE VALUES(18,'Brachioradialis','肱桡肌',replace('肱桡肌是所有肘部肌肉中最长的一块肌肉，它在近端连接在肱骨外侧髁上嵴上，在远端连接在桡骨的茎突旁边。肱桡肌的最大限度缩短导致了肘的完全屈曲，并使前臂旋转至将近中线位置。\nEMG 研究显示，肱桡肌是一块主要的肘屈肌，尤其是在巨大阻力下进行快速运动的时候。','\n',char(10)),'手臂,肘屈肌','[]');
